import React, { useState, useEffect } from "react";
import axios from "axios"; // Import the Axios HTTP client

async function getShirt() {
  await axios
    .get(`http://localhost:5000/api/gets/${"Shirt"}`)
    .then((response) => {
      return response.data[0].color + " Shirt";
    })
    .catch((error) => {
      console.error(error);
    });
}
async function getPant() {
  let strPant = "No Pant in closet";
  await axios
    .get(`http://localhost:5000/api/gets/${"Pant"}`)
    .then((response) => {
      strPant = response.data[0].color + " Pant";
      return strPant;
    })
    .catch((error) => {
      console.error(error);
    });
}
export default function Generate() {
  const [data, setData] = useState(0); // Initialize the state variable to hold the data

  const handleClick = () => {
    // Send a GET request to the server to retrieve the data from the database
    setData("You should wear " + getShirt() + " on top of " + getPant()); // Update the state variable with the data
  };
  const handleChange = (event) => {
    const p = getPant();
    const s = getShirt();
    while (!p && !s) {}
    setData("You should wear " + getShirt() + " on top of " + getPant());
  };
  return (
    <div>
      <button onClick={handleClick}>Get Data</button>
      <textarea
        type="text"
        value={this.data ? <em> Loading... </em> : this.data}
        onChange={handleChange}
      />
    </div>
  );
}
